const Navbar = () => {
  const [button, setButton] = useState(true);

  const [click, setNavIconClick] = useState(false);

  const icon1 = "../Icons/angle-down-solid.svg";
  const icon2 = "../Icons/align-justify-solid.svg";

  const closeMenu = () => setNavIconClick(false);
  const handleIconClick = () => {
    // console.log("hiiii");
    setNavIconClick(!click);
  };

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  window.addEventListener("resize", showButton);

  return (
    <>
      <nav className="navbar">
        <div className="nav-bar-container">
          <Link to="/" className="nav-bar-logo">
            NL `{" "}
            <object data="../Icons/42-group-brands.svg" width="30" height="30">
              {" "}
            </object>
            `
          </Link>
          <div className="nav-bar-icon" onClick={handleIconClick}>
            {/* <object data={iconFile} width="59" height="50"></object> */}
            <img src={click ? icon1 : icon2} width="49" height="40"></img>
          </div>
          <ul className={click ? "nav-menu active" : "nav-menu"}>
            <li className="nav-item">
              <Link to="/" className="nav-links" onClick={closeMenu}>
                {" "}
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/about" className="nav-links" onClick={closeMenu}>
                {" "}
                About us
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/flash-cards" className="nav-links" onClick={closeMenu}>
                {" "}
                Flash Cards
              </Link>
            </li>
            {/* <li className="nav-item">
              <Link
                to="/sign-up"
                className="nav-link-signup"
                onClick={closeMenu}
              >
                {" "}
                Sighn up
              </Link>
            </li> */}
            <li className="nav-item">
              <Link to="/test" className="nav-links" onClick={closeMenu}>
                {" "}
                Take test
              </Link>
            </li>
          </ul>
          {button && <Button buttonStyle="btn--outline">Sighn up</Button>}
        </div>
      </nav>
    </>
  );
};

export default Navbar;
