import { React, useEffect, useContext, useState } from "react";
import "./Profile.css";
import Test from "./Test";
import AuthContext from "./../pages/context/AuthProvider";

const Profile = ({ data }) => {
  const user = useContext(AuthContext);
  const [maxScore, setMaxScore] = useState("");
  const [lastScore, setLastScore] = useState("");

  const ApiUrl = "http://127.0.0.1:5000/users/" + user.auth[0] + "/score";
  useEffect(() => {
    console.log(user.auth);
    if (user.auth[0] !== "") {
      fetch(ApiUrl)
        .then((res) => res.json())
        .then((data) => {
          let temp = NaN;
          data.map((score) => {
            if (Number.isNaN(temp) || parseInt(score.score) > temp) {
              temp = parseInt(score.score);
              console.log(temp);
            }

            if (temp !== NaN) {
              setMaxScore(temp);
              setLastScore(score.score);
            }
          });
        });
    }
  }, []);

  return (
    <div className="profile-container">
      <h1>Users's profile: {user.auth[1]}</h1>
      <h1>max score: {maxScore}</h1>
      <h1>Latest Score: {lastScore}</h1>
      <div className="profile-text-card">
        <div className="user-score-info"></div>
      </div>
    </div>
  );
};

export default Profile;
