import { React, useEffect, useContext, useState } from "react";
import "./Profile.css";
import Test from "./Test";
import AuthContext from "./../pages/context/AuthProvider";

const Profile = ({ data }) => {
  const user = useContext(AuthContext);
  const [maxScore, setMaxScore] = useState(0);
  const [lastScore, setLastScore] = useState(0);

  const ApiUrl = "http://127.0.0.1:5000/users/" + user[0] + "/score";
  useEffect(() => {
    fetch(ApiUrl)
      .then((res) => res.json())
      .then((data) => {
        data.map((score) => {
          if (maxScore < parseInt(score.score)) {
            setMaxScore(parseInt(score.score));
          }

          setLastScore(parseInt(score.score));

          console.log(score.score);
        });
      });
  }, []);

  return (
    <div className="profile-container">
      <h1>Users's profile</h1>
      <h1>max score: {maxScore}</h1>
      <h1>Latest Score: {lastScore}</h1>
      <div className="profile-text-card">
        <div className="user-score-info"></div>
      </div>
    </div>
  );
};

export default Profile;
