import React from "react";
import "./Influencer.css";
import ReactDOM from "react-dom";
import Carousel from "react-elastic-carousel";
import Item from "./Item";
import Avatar from "../pictures/avatar.jpeg";
import Tom from "../pictures/Tom Bachik.webp";

const breakPoints = [
  { width: 1, itemsToShow: 1 },
  { width: 550, itemsToShow: 2 },
  { width: 768, itemsToShow: 3 },
  { width: 1200, itemsToShow: 4 },
];
const Influencer = () => {
  return (
    <div className="influencer">
      {/* <div></div> */}
      <h1 style={{ textAlign: "center" }}>Get to know famous nail techs:</h1>
      <div>
        <Carousel breakPoints={breakPoints}>
          <Item>
            <div className="inf-card">
              <div>
                <h1 className="inf-name">Tom Bachik</h1>
              </div>
              <div className="img-container">
                <img src={Avatar} alt="Avatar" className="inf-avatar"></img>
              </div>
              <div className="inf-info">
                <p>hhfhfhfhfhhhffhfh</p>
              </div>
            </div>
          </Item>
          <Item>Two</Item>
          <Item>Three</Item>
          <Item>Four</Item>
          <Item>Five</Item>
          <Item>Six</Item>
          <Item>Seven</Item>
          <Item>Eight</Item>
        </Carousel>
      </div>
    </div>
  );
};
export default Influencer;
